import config from 'config';
import mongoose from 'mongoose';
import Tx from '../../models/tx';
export default async function handler(req, res) {
  if (mongoose.connection._readyState === 0) {
    try {
      await mongoose.connect(config.mongo.uri, config.mongo.options);
    } catch (e) {
      res.json({
        error: 'Internal error. Please try your request again.'
      }).status(500);
      process.exit();
    }
  }

  let skip = Number(req.query.skip) || 0;
  let limit = Number(req.query.limit) || 20;
  let sortOrder = req.query.reverse ? -1 : 1;
  let blockHash = req.query.blockHash;

  try {
    const txs = await Tx.find({
      blockHash
    }).limit(limit).skip(skip).sort({
      timestamp: sortOrder ? -1 : 1
    });
    res.json(txs);
  } catch (e) {
    console.error(e);
    res.json({
      error: 'Internal error. Please try your request again.'
    }).status(500);
  }
}
